mapper.select:
SELECT surveyno, topic, startdate, enddate, poster, cnt, continueyn
FROM survey 
WHERE surveyno=#{surveyno}

mapper.update:
UPDATE survey 
SET topic=#{topic}, startdate=#{startdate}, enddate=#{enddate}, cnt=#{cnt}
WHERE surveyno=#{surveyno}

mapper.select:
SELECT COUNT(*) as cnt FROM survey
WHERE surveyno=#{surveyno}
WHERE surveyno=#{surveyno} AND (topic LIKE '%' || #{word} || '%' OR poster LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')

SELECT surveyno, topic, startdate, enddate, poster, cnt, continueyn, word, r FROM ( SELECT surveyno, topic, startdate, enddate, poster, cnt, continueyn, word, rownum as r FROM ( SELECT surveyno, topic, startdate, enddate, poster, cnt, continueyn, word FROM survey
WHERE surveyno=#{surveyno}
WHERE surveyno=#{surveyno} AND (topic LIKE '%' || #{word} || '%' OR poster LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')
ORDER BY surveyno DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}
