mapper.select:

SELECT sur_itemno, surveyno, itemseq, item, itemfile, itemcnt
FROM surveyitem 
WHERE sur_itemno=#{sur_itemno}

mapper.update:
UPDATE surveyitem 
SET item=#{item}, itemcnt=#{itemcnt}
WHERE sur_itemno=#{sur_itemno}

UPDATE surveyitem 
SET itemcnt = itemcnt + 1
WHERE sur_itemno =#{sur_itemno} and surveyno =#{surveyno}

mapper.select:
SELECT COUNT(*) as cnt
FROM surveyitem
WHERE surveyno = #{surveyno}

SELECT COUNT(*) as cnt FROM surveyitem
WHERE surveyno=#{surveyno}
WHERE surveyno=#{surveyno} AND (item LIKE '%' || #{word} || '%' OR itemfile LIKE '%' || #{word} || '%' OR word LIKE '%' || #{word} || '%')
